INSERT INTO data_tipo_jornadas (nombre, descripcion) VALUES
    ('Jornada de Tiempo Completo', 'Jornada laboral que abarca la totalidad de las horas laborables estándar en una semana.'),
    ('Jornada de Tiempo Parcial', 'Jornada laboral con un número de horas inferior al tiempo completo.'),
    ('Jornada de Turnos Rotativos', 'Jornada en la que los empleados trabajan en diferentes turnos según un horario establecido.'),
    ('Jornada de Turnos Fijos', 'Jornada en la que los empleados trabajan siempre en el mismo turno, ya sea de mañana, tarde o noche.'),
    ('Jornada Nocturna', 'Jornada laboral que se realiza durante la noche, generalmente con un pago adicional por trabajar en este horario.'),
    ('Jornada Acumulativa', 'Jornada en la que el empleado trabaja más horas en algunos días y menos en otros, pero cumple con el total semanal.');
